visit_limit();

class Copyright extends Base {
	static function frameworkLogo() {
		$logo_content = '';
		header("Content-type: image/gif");
		echo base64_decode($logo_content);
	}
	
	/**
	 * LeaPHP框架版本号
	 *
	 */
	static function frameworkVersion() {
		return "Version " . self::$framework_version;
	}

	/**
	 * LeaPHP版权信息
	 *
	 */
	static function frameworkCopyright() {
		$copyright .= "Powered by hliang@Neusoft-DMED Email:<a href='mailto:huang.liang@neusoft.com' target='_blank'>huang.liang#Neusoft.com</a>";
		return $copyright;
	}

	static function frameworkLatestVersion() {
		$content = 'Get latest version: <a href="http://leaphp.net" target="_blank" style="color:#00F; text-decoration:none;">http://leaphp.net</a>';
		return $content;
	}
	
	static function frameworkDefaultPage() {
		$content = '<title>Welcome to LeaPHP default page !</title>
			<div style="font-family:Arial; font-size:13px; padding:10px; border:1px dashed #999; background:rgb(199,223,255)">
			<h1>Welcome to LeaPHP default page !</h1>
			<p>' . self::frameworkCopyright() . '</p>
			<p>' . self::frameworkLatestVersion() . '</p>
			<p>' . self::frameworkVersion() . '</p></div>';
		echo $content;	
	}
}
